import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Chart from "./chart";
import Select from "react-dropdown-select";

const styles = theme => ({
  "chart-container": {
    height: 400
  }
});

let updateInterval = 1000

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      num_values: 30,
      labels: [],
      data: [],
      lineChartData: {
        labels: [],
        datasets: [
          {
            type: "line",
            label: "test",
            backgroundColor: "rgba(0, 0, 0, 0)",
            borderColor: this.props.theme.palette.primary.main,
            pointBackgroundColor: this.props.theme.palette.secondary.main,
            pointBorderColor: this.props.theme.palette.secondary.main,
            borderWidth: "2",
            lineTension: 0.45,
            data: []
          }
        ]
      },
      lineChartOptions: {
        responsive: true,
        maintainAspectRatio: false,
        tooltips: {
          enabled: true
        },
        scales: {
          xAxes: [
            {
              ticks: {
                autoSkip: true,
                maxTicksLimit: 10
              }
            }
          ]
        }
      }
    };
  }

  setValues = selectValues => this.setState({ selectValues });

  updateChart = e => {
    this.setState({data: this.state.data.push(Math.round(Math.random()*100))})
    this.setState({labels: this.state.labels.concat(this.state.labels.length)})
    let data = this.state.data;
    let labels = this.state.labels;

    const oldDataSet = this.state.lineChartData.datasets[0];
    const newDataSet = { ...oldDataSet};
    // newDataSet.data.push(Math.round(Math.random()*100));


    const newChartData = {
      ...this.state.lineChartData,
      datasets: [{...newDataSet, data: data.slice(-this.state.num_values)}],
      labels: labels.slice(-this.state.num_values)
    };
    this.setState({ lineChartData: newChartData });
  }

  componentDidMount() {
    setInterval( this.updateChart,
    updateInterval)
  }

  render() {
    const { classes } = this.props;

    return (
      <div className={classes["chart-container"]}>
        <Chart
          data={this.state.lineChartData}
          options={this.state.lineChartOptions}
        />
        <Select options={[10,30,60]} onChange={(values) => this.setValues(values)} />
      </div>
    );
  }
}

export default withStyles(styles, { withTheme: true })(App);
