class Toggle extends React.Component {

    constructor(props) {
        function ToggleGroup() {
            const Button = styled.button`
            background-color: black;
            color: white;
            font-size: 20px;
            padding: 10px 60px;
            border-radius: 5px;
            margin: 10px 0px;
            cursor: pointer;
            &:disabled {
                color: grey;
                opacity: 0.7;
                cursor: default;
            }
            `;

            const ButtonToggle = styled(Button)`
            opacity: 0.6;
            ${({ active }) =>
                active &&
                `
                opacity: 1;
            `}
            `;
            const ButtonGroup = styled.div`
            display: flex;
            `;

            const types = ['Cash', 'Credit Card', 'Bitcoin'];
            const [active, setActive] = useState(types[0]);

            return (
                <ButtonGroup>
                {types.map(type => (
                    <ButtonToggle
                    key={type}
                    active={active === type}
                    onClick={() => setActive(type)}
                    >
                    {type}
                    </ButtonToggle>
                ))}
                </ButtonGroup>
            );
        }
    }


    render() {
        return (<ToggleGroup/>)
    }
}

export default Toggle;
