import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Chart from "./chart";

const styles = theme => ({
  "chart-container": {
    height: 400
  }
});

let updateInterval = 1000

class App extends React.Component {
  state = {
    meta: {
      ticks: 10,
      lab: []
    },
    lineChartData: {
      labels: [],
      datasets: [
        {
          type: "line",
          label: "test",
          backgroundColor: "rgba(0, 0, 0, 1)",
          borderColor: this.props.theme.palette.primary.main,
          pointBackgroundColor: this.props.theme.palette.secondary.main,
          pointBorderColor: this.props.theme.palette.secondary.main,
          borderWidth: "2",
          lineTension: 0.45,
          data: []
        }
      ]
    },
    lineChartOptions: {
      responsive: true,
      maintainAspectRatio: false,
      tooltips: {
        enabled: true
      },
      scales: {
        xAxes: [
          {
            ticks: {
              autoSkip: true,
              maxTicksLimit: 10
            }
          }
        ]
      }
    }
  };

  componentDidMount() {
    setInterval( this.updateChart = e => {
      const oldDataSet = this.state.lineChartData.datasets[0];
      const newDataSet = { ...oldDataSet };
      let len = this.state.lineChartData.labels.length;
      newDataSet.data.push(Math.round(Math.random()*100));
      // newDataSet.data.shift();
      const newLab = this.lab.concat(this.lab.length+1)
      this.setState({lab: newLab})

      const newChartData = {
        ...this.state.lineChartData,
        datasets: [newDataSet],
        labels: this.lab.slice(this.ticks > this.lab.length ? -this.lab.length : -this.ticks)
      };
      this.setState({ lineChartData: newChartData });
      console.log(this.state.lineChartData.labels)
    },
    updateInterval)
  }

  render() {
    const { classes } = this.props;

    return (
      <div className={classes["chart-container"]}>
        <Chart
          data={this.state.lineChartData}
          options={this.state.lineChartOptions}
        />
      </div>
    );
  }
}

export default withStyles(styles, { withTheme: true })(App);
