import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Chart from "./chart";

const styles = theme => ({
  "chart-container": {
    height: 400
  }
});

let updateInterval = 1000;
let numTicks = 20;

class App extends React.Component {
  state = {
    meta: {
      ticks: numTicks,
      lab: [],
      dat: []
    },
    lineChartData: {
      labels: [],
      datasets: [
        {
          type: "line",
          // label: null,
          backgroundColor: "rgba(0, 0, 0, 1)",
          borderColor: this.props.theme.palette.primary.main,
          pointBackgroundColor: this.props.theme.palette.secondary.main,
          pointBorderColor: this.props.theme.palette.secondary.main,
          borderWidth: "2",
          lineTension: 0.45,
          data: []
        }
      ]
    },
    lineChartOptions: {
      responsive: true,
      maintainAspectRatio: false,
      tooltips: {
        enabled: true
      },
      scales: {
        xAxes: [
          {
            ticks: {
              autoSkip: true,
              maxTicksLimit: numTicks
            }
          }
        ]
      }
    }
  };

  componentDidMount() {
    setInterval( this.updateChart = e => {
      console.log(this.state.meta.dat);
      const newDat = this.state.meta.dat;
      const newLab = this.state.meta.lab;

      const newNum = Math.round(Math.random()*100);

      newDat.push(newNum);
      newLab.push(this.state.meta.lab.length+1);

      const newMeta = {...this.state.meta, lab: newLab, dat: newDat}
      this.setState({meta: newMeta})

      // const dat = this.state.meta.data;

      const oldDataSet = this.state.lineChartData.datasets[0];
      const newDataSet = { ...oldDataSet };
      newDataSet.data.push(newNum);
      if (newDataSet.data.length > this.state.meta.ticks) {
        newDataSet.data.shift();
      }
      // const newLab = this.meta.lab.concat(this.lab.length+1)
      // this.setState({lab: newLab})

      const newChartData = {
        ...this.state.lineChartData,
        datasets: [newDataSet],
        labels: this.state.meta.lab.slice(this.state.meta.ticks > this.state.meta.lab.length ? -this.state.meta.lab.length : -this.state.meta.ticks)
      };
      this.setState({ lineChartData: newChartData });
      console.log(this.state.lineChartData.labels)
    },
    updateInterval)
  }

  render() {
    const { classes } = this.props;

    return (
      <div className={classes["chart-container"]}>
        <Chart
          data={this.state.lineChartData}
          options={this.state.lineChartOptions}
        />
      </div>
    );
  }
}

export default withStyles(styles, { withTheme: true })(App);
